export const GAME_STATE_INIT = 'GAME_STATE_INIT'
export const GAME_STATE_NEW_ROOM = 'GAME_STATE_NEW_ROOM'
export const GAME_STATE_PLAYER_JOIN = 'GAME_STATE_PLAYER_JOIN'



export function createNewGame(state) {
    return {type: GAME_STATE_NEW_ROOM, state}
}

export function playerJoin(state) {
    return {type: GAME_STATE_PLAYER_JOIN, state}
}
