import * as BABYLON from "babylonjs/babylon";
import skybox_nx from "resources/skybox/cloudy/skybox_nx.jpg";
import skybox_ny from "resources/skybox/cloudy/skybox_ny.jpg";
import skybox_nz from "resources/skybox/cloudy/skybox_nz.jpg";
import skybox_px from "resources/skybox/cloudy/skybox_px.jpg";
import skybox_py from "resources/skybox/cloudy/skybox_py.jpg";
import skybox_pz from "resources/skybox/cloudy/skybox_pz.jpg";

class SceneBuilder {
  constructor(canvas, scene) {
    this.canvas = canvas;
    this.scene = scene;
    this.engine = scene.getEngine();
  }

  build() {
    const skybox = BABYLON.Mesh.CreateBox("skyBox", 800.0, this.scene);
    const skyboxMaterial = new BABYLON.StandardMaterial("skyBox", this.scene);
    const aSkybox = [
      skybox_px,
      skybox_py,
      skybox_pz,
      skybox_nx,
      skybox_ny,
      skybox_nz
    ];
    skyboxMaterial.backFaceCulling = false;
    skyboxMaterial.reflectionTexture = BABYLON.CubeTexture.CreateFromImages(
      aSkybox,
      this.scene
    );
    skyboxMaterial.reflectionTexture.coordinatesMode =
      BABYLON.Texture.SKYBOX_MODE;
    skyboxMaterial.diffuseColor = new BABYLON.Color3(0, 0, 0);
    skyboxMaterial.specularColor = new BABYLON.Color3(0, 0, 0);
    skyboxMaterial.disableLighting = true;
    skybox.material = skyboxMaterial;
  }
}

export default SceneBuilder;
