import { GAME_STATE_NEW_ROOM, GAME_STATE_PLAYER_JOIN } from "action/GameActions";
import update from "react-addons-update";

const gamestate = function(state = {}, action) {
  switch (action.type) {
    case GAME_STATE_PLAYER_JOIN:
      console.log(GAME_STATE_PLAYER_JOIN, action);
      return update(state, {step: {
        $set: action.payload.step
      }, players: {
        $set: action.payload.players
      }, roominfo: {
        $set: action.payload.roominfo
      }});
    default:
      return state;
  }
};

export default gamestate;
