import { UPDATE_PLAYER } from "action/PlayerActions";
import update from "react-addons-update";

const player = function(state = {id: '', name: ''}, action) {
  switch (action.type) {
    case UPDATE_PLAYER:
      console.log(UPDATE_PLAYER, action);
      return update(state, {id: {
        $set: action.player.id
      }, name: {
        $set: action.player.name
      }});
    default:
      return state;
  }
};

export default player;
