import { UPDATE_PLAYER } from "action/PlayerActions";
import update from "react-addons-update";

const player = function(state = {fb_client_id: '', fb_client_name: ''}, action) {
  switch (action.type) {
    case UPDATE_PLAYER:
      console.log(UPDATE_PLAYER, action);
      return update(state, {fb_client_id: {
        $set: action.player.fb_client_id
      }, fb_client_name: {
        $set: action.player.fb_client_name
      }});
    default:
      return state;
  }
};

export default player;
