import { UPDATE_ROOM_LIST } from "action/RoomListActions";
import update from "react-addons-update";

const rooms = (state = [], action) => {
  switch (action.type) {
    case UPDATE_ROOM_LIST:
      console.log(UPDATE_ROOM_LIST, action);
      return update(state, {$set: action.rooms});
    default:
      return state;
  }
};

export default rooms;
