import { CHANGE_ROUTE } from "action/RouteActions";
import update from "react-addons-update";

const route = (state = {name: "home"}, action) => {
  switch (action.type) {
    case CHANGE_ROUTE:
      console.log(CHANGE_ROUTE, action);
      return update(state, { name: {
        $set: action.routeName
      }});
    default:
      return state;
  }
};

export default route;